rss.onEnter:
  	setRefreshTime(5000);
  	refreshCnt = 12;
  	lastRefresh = date("%s");
  	refreshFreq = 60;
	loadXMLFile( "%%selfUrl%%");
	SwitchViewer(7);

rss.onRefresh:
	refreshCnt = refreshCnt - 1;
    if ((date("%s") - lastRefresh)  >= refreshFreq) {
    	lastRefresh = date("%s");
    	download_ok = loadXMLFile( "%%selfUrl%%");
	    redrawDisplay();
    }
    else {
    	redrawDisplay();
	}

mediaDisplay.onUserInput:
			userInput = currentUserInput();
			if ( userInput == "R" ) {
				url = getItemInfo(getFocusItemIndex(), "epg_link");
				
				
				jumpToLink("epg_link");				
				redrawDisplay();
				"true";
			} else if ( userInput == "1" ) {
				streamlink = getItemInfo(-1, "link");
    			playItemURL(streamlink, 10, 16);
    			/*playItemURL("http://127.0.0.1/media/sda1/xKartina/playChannel.php?id=8", 0);*/
    			"true";
			} else if ( userInput == "ENTR" ) {
				curId = getItemInfo(getFocusItemIndex(), "id");
				print("-------------------curID: ",curId );
				if("1" != ALLOW_EROTIC && (curId  == "257" || curId  == "155" || curId  == "159" || curId  == "161")) {
				 	inputPass = getInput("passWord", "doModal");
				 	print("-------------------inputPass : ",inputPass);
	      			if (inputPass != NULL) {
			      		url = getItemInfo(getFocusItemIndex(), "link") + "&pkey=" + inputPass;
			      		print("-------------------url : ",url);
			      		playItemURL(url,0);
						/*jumpToLink("epg_link");*/				
						redrawDisplay();

			      	}
	      			redrawDisplay();
	      			"true";				
				}
				else {
					"false";
				}

      		} else {
				"false";
			}
