rss.channel:
  
	
		index = getFocusItemIndex();
		description = getItemInfo(index, "description"); 
		redrawDisplay();
	
	
			userInput = currentUserInput();
			str = userInput;
			if ( userInput == "R" ) {

					url = getItemInfo(index, "epg_link");				
					jumpToLink("epg_link");
					description = url;
					redrawDisplay();
					"true";

			} else {
				index = getFocusItemIndex();
				if ( userInput == "D" ) {
					index = index+1;
				}
				if ( userInput == "U" ) {
					if(index > 0){
						index = index - 1;
					}
				}
				
				description = getItemInfo(index, "description"); 
							
					
				if(null != description) {
					redrawDisplay();
					"false";
				}
			}
			
		
		
			
			

			
				
					description;
				
			
		

